from typing import List

from pinferencia import Server, task


def return_text(data: List[str]) -> List[str]:
    return ["abcdefg"]


def return_json(data: list) -> List[dict]:
    return [{"a": 1, "b": 2}]


def return_table(data: List[str]) -> List[List[dict]]:
    return [[{"a": 1, "b": 2}, {"a": 3, "b": 4}]]


def return_invalid_table(data):
    return [[{"a": 1, "b": 2}, {"a": 3, "b": 4}, 1, 2]]


def return_image(data: List[str]):
    return [
        ""  # noqa
    ]


def raise_error(data: str):
    raise Exception("Error")


service = Server()
service.register(
    model_name="invalid-task-model",
    model=return_text,
    metadata={"task": "invalid"},
)
service.register(
    model_name="return-text-model",
    model=return_text,
    version_name="v1",
    metadata={"task": task.TEXT_TO_TEXT},
)
service.register(
    model_name="return-image-model",
    model=return_image,
    version_name="v1",
    metadata={"task": task.TEXT_TO_IMAGE},
)
service.register(
    model_name="return-json-model",
    model=return_json,
    version_name="v1",
    metadata={"task": task.TEXT_TO_TEXT},
)
service.register(
    model_name="return-table-model",
    model=return_table,
    version_name="v1",
    metadata={"task": task.TEXT_TO_TEXT},
)
service.register(
    model_name="return-invalid-table-model",
    model=return_invalid_table,
    version_name="v1",
    metadata={"task": task.TEXT_TO_TEXT},
)
service.register(
    model_name="return-large-result",
    model=lambda _: ["message " * 100],
    version_name="v1",
    metadata={"task": task.TEXT_TO_TEXT},
)
service.register(
    model_name="return-error-message",
    model=lambda _: "Error message",
    version_name="v1",
    metadata={"task": task.TEXT_TO_TEXT},
)
service.register(
    model_name="return-500",
    model=raise_error,
    version_name="v1",
    metadata={"task": task.TEXT_TO_TEXT},
)
service.register(
    model_name="custom-title-description",
    model=return_text,
    version_name="v1",
    metadata={
        "task": task.TEXT_TO_TEXT,
        "display_name": "My Model",
        "description": "This is my model.",
    },
)
